# ! [doc = " Various analyses of the LLVM IR."] use llvm_sys :: analysis ; pub use llvm_sys :: analysis :: LLVMVerifierFailureAction ; use llvm_sys :: prelude :: * ; # [doc = " Verify that a module is valid, taking the specified action if not."] # [doc = ""] # [doc = " Optionally returns a human-readable description of any invalid constructs,"] # [doc = " which must be disposed with `LLVMDisposeMessage`."] # [no_mangle] pub unsafe extern "C" fn LLVMVerifyModule (M : LLVMModuleRef , Action : LLVMVerifierFailureAction , OutMessage : * mut * mut :: libc :: c_char ,) -> LLVMBool { llvm_sys :: analysis :: LLVMVerifyModule (M , Action , OutMessage) } # [doc = " Verify that a single function is valid, taking the specified action."] # [doc = ""] # [doc = " Useful for debugging."] # [no_mangle] pub unsafe extern "C" fn LLVMVerifyFunction (Fn : LLVMValueRef , Action : LLVMVerifierFailureAction ,) -> LLVMBool { llvm_sys :: analysis :: LLVMVerifyFunction (Fn , Action) } # [doc = " Open a ghostview window displaying the CFG of the given function."] # [doc = ""] # [doc = " Useful for debugging."] # [no_mangle] pub unsafe extern "C" fn LLVMViewFunctionCFG (Fn : LLVMValueRef) { llvm_sys :: analysis :: LLVMViewFunctionCFG (Fn) } # [no_mangle] pub unsafe extern "C" fn LLVMViewFunctionCFGOnly (Fn : LLVMValueRef) { llvm_sys :: analysis :: LLVMViewFunctionCFGOnly (Fn) }